import React, { useState, useEffect } from 'react'
import PostImg from '../PostPage/PostImg'
import '../../Style/Introduce2.css'
import '../../Style/PostBody.css'
import PostAdd from './PostAdd'
import Web3 from 'web3'
import Genesis from '../../abi/GenesisFactory.json'

const { ethereum } = window;
const web3 = new Web3(Web3.givenProvider || "https://rinkeby.infura.io/v3/9aa3d95b3bc440fa88ea12eaa4456161")
const FunGenesis = new web3.eth.Contract(Genesis,'0xB21653754E20bf7A4c93F9a9A3E215e4234b70E1')

function PostBody() {
    const [ManagerAddress, setManagerAddress] = useState('0x000')
    const [GenesisAddress, setGenesisAddress] = useState('')
    const [CompanyName, setCompanyName] = useState('')
    const [Code, setCode] = useState('')
    const [Weight, setWeight] = useState('')
    const [ProductName, setProductName] = useState('')

    const [wallet, setWallet] = useState('false')

    const isMetaMaskInstalled = () => {
        if(ethereum && ethereum.isMetaMask)setWallet('True');
    };
    async function requestWallet() {
        await ethereum.request({ method: 'eth_requestAccounts' });
    }
    async function loadBlockchainData() {
        const accounts = await web3.eth.getAccounts()
        setManagerAddress(accounts)
    }

    useEffect(() => {
        isMetaMaskInstalled()
        requestWallet()
        loadBlockchainData()
    }, [])

    return (
        <>
            <div>{ManagerAddress}</div>
            <div>{CompanyName}</div>
            <div>{Code}</div>
            <div>{Weight}</div>
            <div>{ProductName}</div>
            <div>{wallet}</div>
        <div className='postbody-container'>
                <div className='postbody-box'>
                <div className='postbody-box-left'>
                    <PostImg/>
                </div>
                <div className='postbody-box-right'>
                    <PostAdd/>
                </div>
            </div>
        </div>
        </>
    )
}

export default PostBody
