import React, { useState } from 'react'
import '../../Style/PostImg.css'
import AddPhotoAlternateIcon from '@material-ui/icons/AddPhotoAlternate';

function PostImg({image,setImage}) {
    const[status, setStatus] = useState(false)
    return (
        <>
        {image!=null?
        (
            <label className='postimg-img-box'>
                <img className='postimg-img' src='https://scontent.fhan2-3.fna.fbcdn.net/v/t1.6435-9/119147870_214406846682694_1416479266087649669_n.jpg?_nc_cat=108&ccb=1-4&_nc_sid=8bfeb9&_nc_ohc=0TJOCvBMjHQAX-AY5Da&_nc_ht=scontent.fhan2-3.fna&oh=c393f6a6a81243cb28c00548ad532e62&oe=613665E5'/>
            </label>
        ):(
            <>
            <input type='file' accept='image/*' id="input-image" hidden/>
            <label for="input-image" className='postimg-box'>
                <AddPhotoAlternateIcon style={{fontSize: 200}}/>
            </label>
            </>
        )}
        </>
    )
}

export default PostImg
