import Web3 from 'web3'
import CompanyJson from '../abi/Company.json'
import StaffJson from'../abi/Staff.json'
import ProcessJson from'../abi/'

const CompanyContract = '0x9910FC2B844883cCb8297D2793188aE639CDEBeb'
const StaffContract = '0x7DD97a5557E5708ab497d9BC3b12f2892ECCdc48'
const ProcessContract = '0x122663b4e5c23eC1B7b8432823cdD9c19f914aBA'

const web3 = new Web3("http://127.0.0.1:7545")
const wallet = web3.eth.accounts.wallet;

function createAccount(entropy){
    const data = web3.eth.accounts.create(entropy)
    return data
}

function companyContract(){
    const data = new web3.eth.Contract(CompanyJson,CompanyContract)
    return data
}

function staffContract(){
    const data = new web3.eth.Contract(StaffJson,StaffContract)
    return data
}

function setAccount(key){
    try {
        wallet.add(key)
        console.log(wallet)
    } catch (error) {
        console.log(error)
    }
}


export default {
    createAccount,
    companyContract,
    setAccount,
    staffContract
};
