import Web3 from 'web3'
import Genesis from '../../abi/GenesisFactory.json'
import ProcessBody from './ProcessBody'

const web3 = new Web3(Web3.givenProvider || "https://rinkeby.infura.io/v3/9aa3d95b3bc440fa88ea12eaa4456161")
const GenesisContract = new web3.eth.Contract(Genesis,'0xB21653754E20bf7A4c93F9a9A3E215e4234b70E1')

function PostBody() {

