import React, { useState, useEffect } from 'react'
import { BrowserRouter as Router, Switch, Route} from 'react-router-dom'
import Home from './Page/Home'
import Search from './Page/Search'
import Post from './Page/Post'
import Detail from './Page/Detail'
import Header from './Component/Header'
import Footer from './Component/Footer'
import User from './Page/User'
import Company from './Page/Company'
import Test from './Page/Test'
import WindowLogin from './Component/WindowLogin'
import IPFS from 'ipfs-api'
const ipfs = new IPFS({ host: 'ipfs.infura.io', port: 5001, protocol: 'https' });

function App() {
  const [image, setImage] = useState(null)
  const [account, setAccount] = useState(null)
  const [windowStatus, setWindowStatus] = useState(false)
  window.onclick = function(event) {
    if(event.target.className == 'windowlogin-modal'){
        setWindowStatus(false)
    }
  }

  function clickToAccount(){
    setWindowStatus(true)
  }


  function captureFile(event) {
    event.preventDefault()
    const file = event.target.files[0]
    const reader = new window.FileReader()
    reader.readAsArrayBuffer(file)
    reader.onloadend = () => {
      setImage(Buffer(reader.result))
      console.log('buffer', image)
      ipfs.files.add(Buffer(reader.result), (error, result) => {
        if(error){
          console.log(error)
        }
        setImage(result[0].hash)
      })
    }
  }

  useEffect(() => {
    setAccount(localStorage.getItem('account'))
    console.log(localStorage.getItem('account'))
  }, [])

  return (
    <>
    {/* <img src={`https://ipfs.io/ipfs/${image}`}/>
    <div type='file' onChange={captureFile}>ello</div> */}
      <WindowLogin windowStatus={windowStatus} setAccount={setAccount}/>
      <Header account={account} clickToAccount={clickToAccount} setAccount={setAccount}/>
        <Router>
          <Switch>
            <Route path='/search'>
              <Search/>
            </Route>
            <Route path='/post'>
              <Post/>
            </Route>
            <Route path='/detail'>
              <Detail/>
            </Route>
            <Route path='/staff'>
              <User/>
            </Route>
            <Route path='/company'>
              <Company/>
            </Route>
            <Route path='/test'>
              <Test/>
            </Route>
            <Route path='/'>
              <Home clickToAccount={clickToAccount}/>
            </Route>
          </Switch>
        </Router>
      <Footer/>
    </>
  );
}

export default App;
