import React, { useState, useEffect } from 'react'
import PostImg from '../PostPage/PostImg'
import '../../Style/Introduce2.css'
import '../../Style/PostBody.css'
import PostAdd from './PostAdd'
import ProcessBody from './ProcessBody'
import WebThree from '../../Function/WebThree'

const date = new Date()
function PostBody() {
    const[db,setDd] = useState([])
    const[name,setName] = useState('')
    const[code,setCode] = useState('')
    const[weight,setWeight] = useState('')
    const[image,setImage] = useState('')
    const[loadingCircle, setLoadingCircle] = useState(false)

    async function createProcess(){
        setLoadingCircle(true)
        const requestOptions = {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                name:name,
                staff:localStorage.getItem('account'),
                image:image,
                weight:weight,
                processcode:code,
                time:`${date.getFullYear()}-${date.getMonth()}-${date.getDay()} ${date.getHours()}:${date.getMinutes()}:${date.getSeconds()}`
            })
        };
        await fetch("http://localhost:5000/process", requestOptions)
        .then(res => {res.text(); setLoadingCircle(false)})
    }

    async function getDataListProcess(){
        await fetch(`http://localhost:5000/process/${localStorage.getItem("account")}`).then(res => res.json()).then(res => setDd(res))
    }

    async function addToBlokChain(){
        await WebThree.method
            
    }

    useEffect(() => {
        getDataListProcess()
    }, [])

    return (
        <>
        <div className='postbody-container'>
            <div className='postbody-box'>
                <div className='postbody-box-left'>
                    <PostAdd
                        createProcess={createProcess}
                        setName={setName}
                        setCode={setCode}
                        setWeight={setWeight}
                        loadingCircle={loadingCircle}
                    />
                </div>
                <div className='postbody-box-right'>
                    <PostImg/>
                </div>
            </div>
        </div>
        <ProcessBody db={db}/>
        </>
    )
}

export default PostBody
