import Web3 from 'web3'
import CompanyJson from '../abi/Company.json'

const CompanyContract = '0x9910FC2B844883cCb8297D2793188aE639CDEBeb'
const StaffContract = '0x0b47562A80e5625880aa381FD9AF59ca5D5673F0'

const web3 = new Web3(Web3.givenProvider || "http://127.0.0.1:7545")
const wallet = web3.eth.accounts.wallet;

function createAccount(entropy){
    const data = web3.eth.accounts.create(entropy)
    return data
}

function companyContract(){
    const data = new web3.eth.Contract(CompanyJson,CompanyContract)
    return data
}

function setAccount(key){
    try {
        wallet.add(key)
        console.log(wallet)
    } catch (error) {
        console.log(error)
    }
}


export default {
    createAccount,
    companyContract,
    setAccount,
};
