import React, { useState, useEffect } from 'react'
import PostImg from '../PostPage/PostImg'
import '../../Style/Introduce2.css'
import '../../Style/PostBody.css'
import PostAdd from './PostAdd'
import ProcessBody from './ProcessBody'

const date = new Date()
function PostBody() {
    const[name,setName] = useState('')
    const[code,setCode] = useState('')
    const[weight,setWeight] = useState('')
    const[image,setImage] = useState('')

    async function createProcess(){
        const requestOptions = {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                name:name,
                staff:localStorage.getItem('account'),
                image:image,
                weight:weight,
                processcode:code,
                time:`${new Date().getFullYear}`
            })
        };
        await fetch("http://localhost:5000/process", requestOptions).then(res => res.text())
    }

    console.log(`${date.getFullYear()}-${date.getMonth()}-${date.getDay()} ${date.getHours()}:${date.getMinutes()}:`)

    return (
        <>
        <div className='postbody-container'>
            <div className='postbody-box'>
                <div className='postbody-box-left'>
                    <PostAdd
                        createProcess={createProcess}
                        setName={setName}
                        setCode={setCode}
                        setWeight={setWeight}
                    />
                </div>
                <div className='postbody-box-right'>
                    <PostImg/>
                </div>
            </div>
        </div>
        <ProcessBody/>
        </>
    )
}

export default PostBody
