import React, { useState } from 'react'
import '../Style/WindowLogin.css'

function WindowLogin() {
    const [windowLogin, setWindowLogin] = useState(true)
    window.onclick = function(event) {
        if(event.target.className == 'windowlogin-modal'){
            setWindow(false)
        }
    }
    return (
        <>
        {windowLogin?
        (
            <div className={'windowlogin-modal'}>
                <div className='windowlogin-box'>
                </div>
            </div>
        ):(
            <></>
        )}
        </>
    )
}

export default WindowLogin
