import React, { useEffect, useState } from 'react'
import '../../Style/CompanyBody.css'
import CompanyProfile from './CompanyProflie'
import CompanyManage from './CompanyManage'
import WebThree from '../../Function/WebThree'

function CompanyBody() {
    const [db, setDb] = useState([])
    const [staffNum, setStaffNum] = useState('0')
    const [workNum, setWorkNum] = useState('0')

    async function getData(){
        await fetch(`http://localhost:5000/company/${localStorage.getItem('account')}`).then(res => res.json()).then(res => setDb(res[0]))
    }
    async function checkAccount(){
        if(db.id == null){
            await WebThree.companyContract().methods.CreateCompany().send({from: `0x5Bb46Ecc36DB1B4Af38eDa24e0D4633026D8AaD5`, gasPrice:1000000000, gas:100000}).on('receipt', function(receipt){console.log(receipt.Ev)});
        }
    }

    useEffect(() => {
        getData()
        checkAccount()
    }, [])
    return (
        <div className='companybody-container'>
            {db.id == null?
            (
                <>
                </>
            ):(
                <>
                    <CompanyProfile db={db}/>
                    <CompanyManage/>
                </>
            )}
        </div>
    )
}

export default CompanyBody
