        }
    }, [])
    return (
        <>
        {loading?
        (
            <div>dang tai...</div>
        ):(
            <div className='companybody-container'>
                {db.name != null ?
                (
                    <>
                        <CompanyProfile db={db} staffNum={staffNum} workNum={workNum}/>
                        <CompanyManage staffList={staffList} getStaffList={getStaffList}/>
                    </>
                ):(<></>)}
            </div>
        )}
        </>
    )
}

