import Web3 from 'web3'
import CompanyJson from '../abi/Company.json'

const CompanyContract = '0x53aF1c211aD99eac7D0548E052096016919CBFE0'

const web3 = new Web3(Web3.givenProvider || "https://rinkeby.infura.io/v3/9aa3d95b3bc440fa88ea12eaa4456161")
var wallet = web3.eth.accounts.wallet;
wallet.add('9bd998dfee238ee84424a9ec3558a93160c880a913273017aab4d503cee87111');
console.log(x)

function createAccount(entropy){
    const data = web3.eth.accounts.create(entropy)
    return data
}

function companyContract(){
    const data = new web3.eth.Contract(CompanyJson,CompanyContract)
    return data
}

export default {
    createAccount,
    companyContract
};
